import os
import asyncio
from metaapi_cloud_sdk import MetaApi

class DataCollector:
    def __init__(self, token, account_id):
        self.metaapi = MetaApi(token)
        self.account_id = account_id

    async def fetch_market_data(self, assets):
        account = await self.metaapi.metatrader_account_api.get_account(self.account_id)
        await account.deploy()
        await account.wait_connected()
        
        connection = account.get_rpc_connection()
        await connection.connect()
        await connection.wait_synchronized()
        
        market_data = {}
        for asset in assets:
            quote = await connection.get_symbol_price(asset)
            market_data[asset] = quote['bid']
        
        return market_data

